  `MESSAGE_CONTENT` longtext NOT NULL COMMENT '消息内容',
  `OPER_USER` int(10) NOT NULL COMMENT '操作用户',
  `OPER_DATE` datetime NOT NULL COMMENT '操作日期',
  `VIEW_COUNT` int(10) NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `CREATE_DATE` datetime DEFAULT NULL,
  PRIMARY KEY (`MESSAGE_ID`),
  KEY `FK93D745F653E9581A` (`OPER_USER`),
  CONSTRAINT `FK93D745F653E9581A` FOREIGN KEY (`OPER_USER`) REFERENCES `system_user_info` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=85 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='信息管理';

